sys_script_include.script:
var GetValueClient = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetValueClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
  // Define the getEmail function.  
  // Create a GlideRecord for the User table.
  // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
  // Return the email address for the requested record
  getValue: function() {
    var userChoice = new GlideRecord("sys_choice");
    userChoice.get(this.getParameter('sysparm_userChoice'));
    return userChoice.Label + '';
  },
  type: 'GetValueClient'
});
